mapper.insert:
INSERT INTO user (
accountname, phone, email, remark, password, role
) VALUES (
#{accountname}, #{phone}, #{email}, #{remark}, #{password},#{role}
)

mapper.update:
UPDATE user
accountname = #{accountname}, phone = #{phone}, email = #{email}, remark = #{remark}, password = #{password},
WHERE id = #{id, javaType=java.lang.Long, jdbcType=BIGINT}
